mapper.update:
UPDATE transaction
payment_option = #{paymentOption:VARCHAR}, receiver_name = #{receiverName:VARCHAR}, receiver_phone = #{receiverPhone:VARCHAR}, dlvy_addr = #{divyAddr:VARCHAR}, dlvy_request = #{divyRequest:VARCHAR}, dlvy_date = #{divyDate:DATE}
tran_no = #{tranNo}

mapper.select:
	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
			FROM	( 





			) inner_table
			WHERE ROWNUM <= #{search.endRowNum} )
	WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

mapper.sql:
SELECT DISTINCT transaction.tran_no, transaction.prod_no AS prod_no, inventory.amount AS amount, price*inventory.amount AS total_price, price, transaction.order_date, receiver_name, receiver_phone, tran_status_code FROM transaction, product, inventory
transaction.prod_no = product.prod_no AND transaction.tran_no = inventory.tran_no AND buyer_id = #{buyerId}
ORDER BY transaction.order_date

mapper.select:
SELECT product.amount - count(inventory.amount) AS inventory FROM product,transaction, inventory
product.prod_no = transaction.prod_no AND transaction.tran_no = inventory.tran_no AND product.prod_no = ${value}
GROUP BY product.amount;
